import React from 'react'
import { Link } from 'react-router-dom'
import UpdateSelectedTags from "./UpdateSelectedTags";

export default function EventCard({ _id, title, startDate, place, details, tags }) {
	const eventTime = startDate.slice(11, 16)
	const formattedDate = new Date(startDate).toDateString()

	return (
		<div>

			<Link to={`/events/${_id}`}>
				<h3>{title}</h3>
			</Link>

			<p>Date: {formattedDate}</p>
			<p>Time: {eventTime}</p>
			<p>Place: {place}</p>
			<p>Details: {details}</p>

			{/* <UpdateSelectedTags /> */}
		</div>
	)
}
