import React, { useEffect, useState } from "react"
import axios from "axios"
import { Link } from 'react-router-dom'
import TagSearchBar from '../components/TagSearchBar'

export default function EveryItem() {
    const [events, setEvents] = useState([])
    const [notes, setNotes] = useState([])
    const [audios, setAudios] = useState([])
    const [search, setSearch] = useState("");

    const storedToken = localStorage.getItem('authToken')
    const getAllItems = () => {
        axios.get("/api/allitems", { headers: { Authorization: `Bearer ${storedToken}` } })
            .then(response => {
                setEvents(response.data.createdEvents)
                setNotes(response.data.createdNotes)
                setAudios(response.data.createdAudios)
            })
            .catch(err => console.log(err))
    }

    useEffect(() => {
        getAllItems()
    }, [])

    const sortByTitle = () => {
        const sortedByName = [...events, ...notes, ...audios]
        sortedByName.sort(function (a, b) { return a.name.localeCompare(b.name) })
        setContacts(sortedByName)
      }



    return (
        <>
            <h1>All your items</h1>
            <p>Search by tag</p>
            <TagSearchBar searchProp={setSearch} />
            {[...events, ...notes, ...audios]
                .filter((item) =>
                item.tags.toString().toLowerCase().includes(search.toLowerCase()))



                .map((item) => {
                    return (
                        <div key={item._id}>
                            {events.includes(item) &&
                                <Link to={`/events/${item._id}`}>
                                    <h2>{(item.title)}</h2>
                                </Link>}

                            {notes.includes(item) &&
                                <Link to={`/notes/${item._id}`}>
                                    <h2>{(item.title)}</h2>
                                </Link>}

                            {audios.includes(item) &&
                                <Link to={`/audios/${item._id}`}>
                                    <h2>{(item.title)}</h2>
                                </Link>}

                            <p>{(item.date)}</p>
                            <p>{(item.place)}</p>
                            <p>{(item.tags)}</p>
                            <p>{(item.description)}</p>
                        </div>
                    )
                })

            }


