import axios from "axios"

export default function AddNote(props) {

    const [title, setTitle] = useState('')
    const [description, setDescription] = useState('')
    const [tags, setTags] = useState([''])
    const [selectedTags, setSelectedTags] = useState([''])

    const handleSubmit = event => {
        // PREVENT refreshing the pagee 
        event.preventDefault()
        // USING "API" here as you send to the backend
        console.log(title, description, selectedTags)
        axios.post('/api/notes', { title, description, selectedTags })
            .then(response => {
                // console.log("response")
                // reset the form
                setTitle('')
                setDescription('')
                setSelectedTags([''])
                // refresh the list of projects in 'ProjectsList'
                props.getAllNotes()
            })
            .catch(err => console.log(err))
    }

    useEffect(() => {
        axios.get('/api/tags', { tags })
            .then(response => {
               // console.log(response.data)
                setTags(response.data)

            })
            .catch(err => console.log(err))
    }, [])

