import React, { useEffect, useState } from "react"
import axios from "axios"

export default function AddNote(props) {

    const [title, setTitle] = useState('')
    const [description, setDescription] = useState('')
    const [tags, setTags] = useState([3, 5, 7])

    const handleSubmit = event => {
        console.log("rororo")
        // PREVENT refreshing the pagee 
        event.preventDefault()
        // USING "API" here as you send to the backend
        axios.post('/api/notes', { title, description, tags })
            .then(response => {
                console.log("response")
                // reset the form
                setTitle('')
                setDescription('')
                setTags('')
                // refresh the list of projects in 'ProjectsList'
                props.getAllNotes()
            })
            .catch(err => console.log(err))
    }

    useEffect(() => {
        axios.get('/api/tags', { tags })
            .then(response => {
                console.log(response.data)
                setTags(response.data)

            })
            .catch(err => console.log(err))
    }, [])

    return (
        <>
            <h1>Add a Note</h1>
            <form onSubmit={ handleSubmit }>
                <h2>Title</h2>
                <input
                    type="text"
                    value={title}
                    onChange={event => setTitle(event.target.value)}
                />

                <h2>Description</h2>
                <input
                    type="text"
                    value={description}
                    onChange={event => setDescription(event.target.value)}
                />

                <h2>Tags</h2>
                <select name="tags" multiple >
                {console.log(tags)    }
                {tags?.map((tag) => <option key={tag}>{tag}</option>)}

                </select> 

                {/* <input
                    type="text"
                    value={tag}
                    onChange={event => setTag(event.target.value)}
                /> */}
                <button type="submit">Add this Note âž•</button>
            </form>

        </>
    )
}
