import React, { useEffect, useState } from "react"
import axios from "axios"
import { Link } from 'react-router-dom'

export default function EveryItem() {
    const [events, setEvents] = useState([])
    const [notes, setNotes] = useState([])
    const [audios, setAudios] = useState([])
    const [allItems, setAllItems] = useState([])

    const storedToken = localStorage.getItem('authToken')
    const getAllItems = () => {
        axios.get("/api/allitems", { headers: { Authorization: `Bearer ${storedToken}` } })
            .then(response => {
                setEvents(response.data.createdEvents)
                setNotes(response.data.createdNotes)
                setAudios(response.data.createdAudios)
            })
            .catch(err => console.log(err))
    }

    useEffect(() => {
        getAllItems()
    }, [])

    return (
        <>
            <h1>All your items</h1>

            {[...events, ...notes, ...audios].map(item => (
                <div key={(item._id)}>
                    { events.includes(item) &&  <Link to={`/events/${item._id}`}>
                        <h2>{(item.title)}</h2>
                    </Link>}

                    { events.includes(item) &&  <Link to={`/events/${item._id}`}>
                        <h2>{(item.title)}</h2>
                    </Link>}


                    <p>{(item.date)}</p>
                    <p>{(item.place)}</p>
                    <p>{(item.tags)}</p>
                    <p>{(item.description)}</p>
                </div>
            )
            )}


        </>
    )

}
