import React, { useState, useEffect } from 'react'
import { useParams, Link, useNavigate } from 'react-router-dom'
import axios from 'axios'

export default function AudioDetails() {
	const [audio, setAudio] = useState(null)

	const { id } = useParams()
	const navigate = useNavigate()

    useEffect(() => {
		const storedToken = localStorage.getItem('authToken')
		axios.get(`/api/bloburl/${id}`, { headers: { Authorization: `Bearer ${storedToken}` } })
		.then(response => {
			// console.log(response)
			setAudio(response.data)
		})
		.catch(err => console.log(err))
}, [])

	const deleteAudio = () => {
		const storedToken = localStorage.getItem('authToken')
		axios.delete(`/api/bloburl/${id}`, { headers: { Authorization: `Bearer ${storedToken}` } })
			.then(() => {
				navigate('/audios')
			})
			.catch(err => console.log(err))
	}

	return (
		<>
				{audio === null ? <h3>Loaading...</h3> :
				<>
					<h1>Audio Details</h1>
					<h3>{audio.title}</h3>
					<p>{audio?.tags?.join(", ")}</p>
					<Link to={`/bloburl/edit/${audio._id}`}>
						<button>Edit this audio üìù</button>
					</Link>
					<button onClick={deleteAudio}>Delete this audio ‚ùå</button>
				</>
			} 
		</>
	)
}
